css:
@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url(about:home) {
  #brandLogo {
    background-image: url(/*[[image]]*/);
    background-size: auto 300px;
    height: 300px;
    width: 300px;
    margin: -22px auto 1em;
  }

  #aboutMozilla:hover::after {
    content: url(data:image/png;base64,);
    position: absolute;
    top: 12px;
    left: 12px;
  }
}

@-moz-document url-prefix("http://userstyles.org/styles/97957/") {
  #style-settings li:-moz-any(:nth-child(2), :nth-child(3)) {
    display: none;
  }
}

@-moz-document url("chrome://browser/content/browser.xul") {
  x|*:root[stylish-url="about:home"][stylish-month="4"][stylish-date="1"][stylish-hour^="2"]:not([stylish-hour="2"]) x|*#content-deck {
    background-image: url(data:image/png;base64,);
    background-position: 2em 1em;
    background-repeat: no-repeat;
  }

  x|*:root[stylish-url="about:home"][stylish-month="4"][stylish-date="1"] x|*#browser {
    transform: rotate(-4deg);
    margin: 5em 0 -5em;
    box-shadow: 0 0 12px rgba(0, 0, 0, .5);
    animation: tilt ease-in 500ms;
  }

  x|*:root[stylish-url="about:home"][stylish-month="4"][stylish-date="1"] x|*#browser-bottombox {
    position: relative;
  }

  @-moz-keyframes tilt {
    from {
      transform: rotate(0deg);
      margin: 0;
      box-shadow: 0 0 0 rgba(0, 0, 0, .5);
    }

    to {
      transform: rotate(-4deg);
      margin: 5em 0 -5em;
      box-shadow: 0 0 12px rgba(0, 0, 0, .5);
    }
  }
}
