css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("aliexpress.com") {

#top-lighthouse, 
.top-container-bg, .filter-item, 
dt span:hover, .title,
.sub-cate-items a:hover,
.keyword-list a:hover,
.ui-autocomplete li:hover, #pagination-bottom,
.ui-notice, .ui-notice-normal, .ui-notice-prompt,
.rec-industry-right-list, .ui-balloon, .store-header,
.bestselling-title, .show-history, .histogram-height
{
background-color:#AD0000 !important;
}
#price-range-list li, .ui-breadcrumb
{
background:#C97800 !important;
}
.categories-content-title, dt span, 
.keyword-list, .industry-enter a,
.flyout-user-signIn, .flyout-user-signout,
.flyout-visitors-login, .flyout-quick-entry,
.flyout-remind-list, .user-account-main,
.sub-cate-main, .indutry-info, .p-quantity-modified,
.bestsellling-slider-container, 
.slidebox-disabled, .bestsellling-slider-next a,
#pagination-bottom .pos-right .page-cur
{
background-color:#C97800 !important;
}
body, #header, .index-page, .ui-autocomplete,
.narrow-down-area, .detail, .detail-gallery-main,
#pagination-bottom .pos-right a, .detail-wrap,
#pagination-bottom .pos-right .page-start,
.product-attribute-main
{
background-color: #111111 !important;
}
input, select,
.user-account-info
{
background: #000000 !important;
}
.top-lighthouse, .top-container-bg, .header,
#pagination-bottom, .list-item
{
border-bottom:1px #111111 solid !important;
}
.cl-item .cate-name span, .categories-main, input,
.user-account-main, #filter-options .filter-item,
.ui-notice, .ui-notice-normal, .ui-notice-prompt
{
border:1px #111111 solid !important;
}
#pagination-bottom .pos-right a,
#pagination-bottom .pos-right .page-start,
.p-quantity-modified
{
border-color:#C97800 !important;
}
.ui-breadcrumb
{
border-color:#C97800 !important;
}
a, p, span, input, select, strong,
h1, h2, h3, h4, h5, .cate-title, .pos-left,
.ui-notice .ui-notice-body, p.ui-notice .ui-notice-body
{
color: #FFFFFF !important;
}
}
