css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbh.fred26.fr") {

    /* Modifications Carte BigBroth'Hordes */
    
    /* Batiments */
    
    #cases div[class^='bat_']{
        background-color: /*[[batcolor]]*/ !important;
    }

    #cases div.bat_-1{ /*Non déterré*/
        background-color: /*[[batndcolor]]*/ !important;
    }
    
    #cases div.bat_1{ /*Ville*/
        background-color: /*[[batvillecolor]]*/ !important;
    }

    /*Cases inconnues */
    #cases div.nv{
        background-image:none !important;
        background-color:rgba(0,0,0,0.4);
    }
    
}
