css:
@-moz-document url-prefix("http://www.angraal.com/") {
  
  .bodylinewidth {
    width: /*[[forum-width]]*/ !important;
  }
 
  .postbody {
    padding: 1% !important;
    font-size: /*[[text]]*/!important;
    line-height: /*[[line-height]]*/ !important;
    color: /*[[color]]*/;
  }
  
  .quote {
    font-size: /*[[quote]]*/;
  }

  td.row1 {
    background: /*[[color-row1]]*/;
  }

  td.row2 {
    background: /*[[color-row2]]*/;
  }

  ::selection {
    background: /*[[color-selection]]*/;
  }
  
}
