@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("deviantart.com") {
    
    .browse-facet ul li.facet-link-hidden {display: inline !important;}
    
    .browse-facet-toggle-hidden {display: none !important;}
    
    .browse-facet ul li.facet-link-hidden a {
        
        font-size: /*[[fontsize]]*/px !important;
        color: /*[[colorda]]*/ !important;
    }
    
    .browse-facet ul li.facet-link-hidden a:hover {color: #FFFFFF;}
    
    a[href="https://www.deviantart.com/checkout/?mx=premium&subpref=22870_0&point=wayback"] {display: none !important;}

}
