@-moz-document domain("amazon.com"), domain("amazonforum.com"), domain("worker.mturk.com"), domain("amazonbrowserapp.com"), domain("aboutamazon.com") {

/* Option Button _______________________________________________________________*/

.a-accordion .a-accordion-row {
    padding: 3px 0 10px 42px;
}

.a-accordion .a-accordion-row .a-icon-radio-active, .a-accordion .a-accordion-row .a-icon-radio-inactive {
    top: 50%;
    left: 4px;
}

/* On ________________________*/

[role="radio"][aria-checked="true"] i:not([role="img"])
{
	background: url(https://i.ibb.co/0XBzN1G/green-light-off.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ________________________*/

[role="radio"][aria-checked="false"] i:not([role="img"])
{
	background: url(https://i.ibb.co/z5C1bGj/red-light-on.png) no-repeat!important;
	box-sizing: border-box;
}

[role="radio"][aria-checked="false"] i:not([role="img"]), [role="radio"][aria-checked="true"] i:not([role="img"]) {
    background-size: 31px!important;
    height: 36px!important;
    padding-left: 32px!important;
    width: 1px!important;
}

}
