css:
@-moz-document domain("feedly.com")
{
/* Adjust "No Unread Articles" Orange Color */
.hhint > .categoryUnreadCountHint > span {color: #B4B3B2 !important;}

/* Hide Left Pane Junk: Home, Integrations, Shared Collections, Pro Support, Roadmap, Mobile Apps, Privacy Policy, Terms Of Service, Upgrade Button */
#mytab_header, #integrationstab_header, #librarytab_header, div.label.primary.iconless[data-uri="pro/support"], a.label.primary.iconless[href="https://plus.google.com/communities/113648582731838175643"], a.label.primary.iconless[href="http://feedly.com/apps.html"], a.label.primary.iconless[data-uri="privacy"], a.label.primary.iconless[data-uri="terms"], div.button.primary.small {display: none !important;}

/* Hide Title Section Junk: Search Box & Placeholder, Subtitle Extra Info - Keyboard Shortcuts on "All" Page - Fix Unread Count Subtitle Color */
span.hAction.tertiary, #hercule.default, #searchBoxPlaceholder, .feedMetadata {display: none !important;}
DIV.miniRealtimeSign[style="display:none"]:first-child + SPAN.hhint:last-child {color: transparent !important;}
.hhint > .categoryUnreadCountHint {color: #B4B3B2 !important;}

/* Hide Right Sidebar & Unread Refresh Bar */
#sideArea, #realtimeSign {display: none !important;}

/* Adjust Main Section Width */
#feedlyPage {width: 100% !important;}

/* Adjust Article Section Width */
.u4Entry {max-width: 100% !important;}

/* Adjust Full Article view text area width */
.entryBody {max-width: 794px !important;}

/* Adjust Full Article view title area width */
.u100Entry .title {max-width: 793px !important;}

/* Adjust Mark As Read Undo Bar */
#feedlyUndoSign {background-color: #EFEFEF !important; color: #696969 !important; margin-top: 6px !important;}

/* Adjust Welcome Page Width */
#explain {width: 859px !important;}
}
