css:
@-moz-document domain("outlook.live.com") {
/* Adjust Content Block Right - Hide Right Adbar Ads, Hide Right Adbar Block */
#primaryContainer > DIV[style="overflow: hidden; position: absolute; top: 50px; right: 165px; bottom: 0px; left: 0px; height: auto; width: auto;"] {right: 0px !important;}
#owaadbar, #primaryContainer > div._n_p {display: none !important;}

/* Hide Topbar Item: Help */
button#O365_MainLink_Help {display: none !important;}
}

@-moz-document domain("login.live.com"), domain("live.xbox.com"), domain("account.xbox.com") {
/* Hide Sign In Junk: Ad Image, "Microsoft account What's this?", "Can't access your account?" & "Sign in with a single-use code" - "Don't have a Microsoft account?", Footer */
#brandModeTD, #idTd_PWD_UsernameLbl, #idTd_PWD_SubmitCancelTbl + .section {display: none !important;}
#SignUpTD, #footerTD {visibility: hidden !important;}

/* Adjust Sign In Box Position */
#signInTD {margin-left: 165px !important; margin-top: 100px !important;}

/* Adjust Sign In Logo Position */
#i0272 {padding-left: 21px !important; margin-bottom: -10px !important;}

/* Adjust Sign In Button Bottom Margin */
div.section {margin-bottom: 0px !important;}

/* Style Sign In Box */
FORM[name="f1"] {background-color: #f7f7f7 !important; border: 2px solid #e8e8e8 !important; width: 100% !important; padding: 20px !important;}

/* Adjust Footer Height */
#evDiv.footerHeight {height: 0px !important;}

/* Hide Xbox Sign in Message */
BODY > DIV.signInMsg {visibility: hidden !important;}
}
