/* Animation
**************************************/
@keyframes movehover {
	0%	{top:0px;}
	20%	{top:-2px;}
	40%	{top:-3px;}
	60%	{top:-3px;}
	80%	{top:-2px}
	100%{top:0px;}
}
@-webkit-keyframes movehover {
	0%	{top:0px;}
	20%	{top:-2px;}
	40%	{top:-3px;}
	60%	{top:-3px;}
	80%	{top:-2px}
	100%{top:0px;}
}
/* Journal */
#content > div.row.pgf-game-data > div.span5.pgf-info-column > div.easy-block.second-row-block > ul > li:nth-child(1) > a:hover,
#content > div.row.pgf-game-data > div.pgf-info-column.span9 > div.easy-block.second-row-block > ul > li:nth-child(1) > a:hover,
/* Diary */
