#content > div:nth-child(5) > div.span6 > div > ul > li.pull-right > a.transparent.pgf-log-mode.pgf-log-mode-artistic,
#content > div:nth-child(5) > div.span6 > div > ul > li.pull-right > a.transparent.pgf-log-mode.pgf-log-mode-technical,
/* Log mode */
#content > div.row.pgf-game-data > div.pgf-info-column.span5 > div.easy-block.second-row-block > ul > li.pull-right > a.transparent.pgf-log-mode.pgf-log-mode-all,
#content > div.row.pgf-game-data > div.pgf-info-column.span9 > div.easy-block.second-row-block > ul > li.pull-right > a.transparent.pgf-log-mode.pgf-log-mode-all,
#content > div.row.pgf-game-data > div.pgf-info-column.span5 > div.easy-block.second-row-block > ul > li.pull-right > a.transparent.pgf-log-mode.pgf-log-mode-artistic,
#content > div.row.pgf-game-data > div.pgf-info-column.span9 > div.easy-block.second-row-block > ul > li.pull-right > a.transparent.pgf-log-mode.pgf-log-mode-artistic,
#content > div.row.pgf-game-data > div.pgf-info-column.span5 > div.easy-block.second-row-block > ul > li.pull-right > a.transparent.pgf-log-mode.pgf-log-mode-technical,
#content > div.row.pgf-game-data > div.pgf-info-column.span9 > div.easy-block.second-row-block > ul > li.pull-right > a.transparent.pgf-log-mode.pgf-log-mode-technical {
	border: none !important;
	font-size: 0px !important;
	padding-bottom: 0px !important;
