/* Journal_ico
**************************************/
.pgf-journal-tab-button:before {
	content: url('data:image/png;base64,');
}
li.active a.pgf-journal-tab-button:before,
li.active a.pgf-journal-tab-button:hover:before {
	content: url('data:image/png;base64,');
}
/* Diary
**************************************/
.pgf-diary-tab-button:before {
	content: url('data:image/png;base64,');
}
li.active a.pgf-diary-tab-button:before,
li.active a.pgf-diary-tab-button:hover:before {
	content: url('data:image/png;base64,');
}
/* Quests
**************************************/
.pgf-quests-tab-button:before {
	content: url('data:image/png;base64,');
}
li.active a.pgf-quests-tab-button:before,
li.active a.pgf-quests-tab-button:hover:before {
	content: url('data:image/png;base64,');
}
/* Log_mode
**************************************/
.transparent.pgf-log-mode.pgf-log-mode-all:before {
	content: url('data:image/png;base64,');
}
.transparent.pgf-log-mode.pgf-log-mode-artistic:before {
	content: url('data:image/png;base64,');
}
.transparent.pgf-log-mode.pgf-log-mode-technical:before {
	content: url('data:image/png;base64,');
}
/* Update 0.3.20 End
**************************************/
/* Navbar
**************************************/
body .navbar .navbar-inner {
	max-height: 15px !important;
}
.navbar .nav > li > a {
	font-weight: bold !important;
	color: #793 !important;
	text-shadow: 0px 0px 3px black, 0px 0px 5px black !important;
}
li.active .game-link {
	font-weight: bold !important;
}
#time-block {
	font-weight: bold !important;
	font-size: 12px !important;
	color: #08C !important;
}
.navbar .nav > li > a:hover {
	color: #08C !important;
}
.dropdown-menu li > a:hover {
	color: #08C !important;
	background-color: transparent !important;
}
.navbar .nav .active > a {
	background-color: transparent !important;
}
.dropdown-menu .divider {
	border: 1px solid gray !important;
	height: 0px !important;
	background-color: gray !important;
}
.caret, .nav li.dropdown.open a:hover .caret {
	border-top-color: #793 !important;
}
.navbar .dropdown-menu:after {
	border-bottom: 6px solid #793 !important;
}
/* Nav_left_block
**************************************/
.nav-list > .active > a, .nav-list > .active > a:hover {
	color: #793 !important;
	background-color: transparent !important;
}
.nav-list .nav-header {
	color: #FFF !important;
	text-shadow: 0px 0px 3px black, 0px 0px 5px black !important;
	font-size: 12px !important;
}
.nav > li > a {
	text-shadow: 0px 0px 3px black, 0px 0px 5px black !important;
}
.nav > li > a:hover {
	color: #793 !important;
}
/* Arena
**************************************/
.pgf-pvp-ability-ice-probability:hover,
.pgf-pvp-ability-blood-probability:hover,
.pgf-pvp-ability-flame-probability:hover {
	color: #3A87AD !important;
	background-color: transparent !important;
}
div.row:nth-child(4) > div:nth-child(1) > div:nth-child(2) > ul:nth-child(2) > li:nth-child(-n+5):hover,
div.row:nth-child(4) > div:nth-child(3) > div:nth-child(2) > ul:nth-child(2) > li:nth-child(-n+5):hover {
	color: #793 !important;
	background-color: transparent !important;
}
/* Game_page
**************************************/
.angel-ability.pgf-ability-help {
	font-size: 16px;
}
.angel-ability.pgf-ability-drop_item {
	font-size: 12px;
}
.shortests-hero-record:hover {
	color: #793 !important;
	background-color: transparent !important;
}
.progress.progress-info {
	color: #333 !important;
	text-shadow: none !important;
}
.icon-gift {
	background-image: none !important;
}
.icon-gift:before {
	content: url('data:image/png;base64,');
}
.angel-ability.no-energy .icon-tint {
	vertical-align: bottom !important;
}
.pgf-quest-description {
	color: #793 !important;
}
.pgf-quest-action-description {
	color: #08C !important;
}
.quest-icon {
	border: 2px solid gray !important;
	border-radius: 7px !important;
}
body div.block {
	background-color: transparent !important;
	border: 3px double gray !important;
	border-radius: 7px !important;
	padding: .55em !important;
	margin-bottom: 10px !important;
}
body hr {
	border-color: gray !important;
}
.quest-record {
	min-height: 90px !important;
}
.pgf-quest-rewards {
	color: #999 !important;
}
.quest-record.odd {
	padding-bottom: 15px !important;
}
.quests-line {
	border-bottom: 2px solid gray !important;
}
.quests-line:last-child {
	border-bottom: 0px !important;
}
.hero-base-block .record-with-progress:hover,
.hero-base-block .attribute-record:hover,
#hero-bag-block .bag-record:hover,
.log-record:hover,
.diary-record:hover {
	color: #793 !important;
	transition: color 0.3s !important;
	background-color: transparent !important;
}
.hero-equipment-block .equip-record:hover,
.quest-record:hover {
	background-color: transparent !important;
}
.pgf-time.time {
	margin: 0.17em !important;
	font-size: 12px !important;
	color: #793 !important;
}
.diary-record .datetime {
	color: #999 !important;
}
.label,
.badge {
	color: #000 !important;
}
body .progress {
	border: none !important;
}
.nav > li > a {
	color: #A9A9A9 !important;
}
.nav {
	margin-bottom: 5px !important;
}
.nav.nav-tabs {
	border-bottom: none !important;
}
.nav-tabs > .active >  * {
	color: #793 !important;
}
.nav-tabs > .active >  *,
.nav-tabs > li > a:hover {
	border: 1px solid gray !important;
	background-color: transparent !important;
	border-bottom-right-radius: 4px !important;
	border-bottom-left-radius: 4px !important;
}
.dropdown-menu a.common-card-label {
	color: #999 !important;
}
.dropdown-menu a.uncommon-card-label {
	color: #30B030 !important;
}
.dropdown-menu a.rare-card-label {
	color: #0070DD !important;
}
.dropdown-menu a.epic-card-label {
	color: #A335EE !important;
}
.dropdown-menu a.legendary-card-label {
	color: #FF8000 !important;
}
.dropdown-menu a.common-card-label:hover,
.dropdown-menu a.uncommon-card-label:hover,
.dropdown-menu a.rare-card-label:hover,
.dropdown-menu a.epic-card-label:hover,
.dropdown-menu a.legendary-card-label:hover {
	color: #08C !important;
	transition: color 0.3s !important;
}
/* Game_heroes_page
**************************************/
#hero-page-content > div.span8 > ul > li:nth-child(4) > a {
	border: none !important;
}
.ability-name {
	color: #FFF !important;
}
.ability-type {
	color: #793 !important;
}
.hero-ability-record:hover,
.hero-habit-record:hover,
.hero-preferences-record:hover,
.hero-statistics .stat-record:hover {
	color: #793 !important;
	transition: color 0.3s !important;
	background-color: transparent !important;
}
input[type="text"] {
	padding: 0px !important;
	border: none !important;
}
li.hero-habit-record:nth-child(-n+2) > strong:nth-child(1),
li.hero-preferences-record:nth-child(-n+11) > strong:nth-child(1) {
	color: #999 !important;
}
div.span4:nth-child(2) {
	margin-left: 25px !important;
}
.easy-block.hero-statistics {
	margin-right: 5px !important;
}
/* Input
**************************************/
input[type="text"]:focus,
textarea:focus {
	border-color: gray !important;
	box-shadow: none !important;
}
 #id_caption {
	padding: 4px !important;
	border: 1px solid gray !important;
	margin-bottom: 0px !important;
	text-shadow: 0px 0px 3px black, 0px 0px 5px black !important;
}
/* Accounts_new_guild
**************************************/
#id_name,
#id_abbr, 
#id_motto {
	padding: 4px !important;
	border: 1px solid gray !important;
	margin-bottom: 0px !important;
	text-shadow: 0px 0px 3px black, 0px 0px 5px black !important;
}
/* Accounts_profile
**************************************/
#id_nick,
#id_email,
#id_password {
	padding: 4px !important;
	border: 1px solid gray !important;
	margin-bottom: 0px !important;
}
/* Guide_api
**************************************/
pre {
	border: 3px double gray !important;
	color: #FFF !important;
	background-color: transparent !important;
}
code {
	color: #FFF !important;
	background-color: transparent !important;
	border: none !important;
}
.addthis_button_expanded {
	color: #333 !important;
	text-shadow: none !important;
}
/* Bills
**************************************/
.dropdown-toggle.pgf-create-new-bill-buttons {
	border: solid 1px gray !important;
	background-color: transparent !important;
}
/* Community_accounts
**************************************/
.form-inline input {
	padding: 4px !important;
	margin-bottom: 0px !important;
}
/* Community_achievements
**************************************/
.nav-list .divider {
	background-color:  gray !important;
	border-bottom: 1px solid gray !important;
}
.achievement.owned h4,
.achievement.not-owned h4 {
	padding: 10px 10px 0px 3px !important;
	background-color: transparent !important;
}
.achievement.owned,
.achievement.not-owned {
	padding: 0px 0px 5px 10px !important;
	border: 3px double gray !important;
	background-color: transparent !important;
}
div[style="position: relative; top: -20px; font-size: 14px;"] {
	color: #333 !important;
	text-shadow: none !important;
}
/* Game_world>>>
**************************************/
.nav-pills > .active > a {
	border: 1px solid gray !important;
	color: #793 !important;
	transition: color 0.3s !important;
	background-color: transparent !important;
}
.nav-pills > .active > a, .nav-pills > .active > a:hover {
	border: 1px solid gray !important;
	color: #793 !important;
	transition: color 0.3s !important;
	background-color: transparent !important;
}
.nav > li > a:hover,
body .nav-tabs > li > a.transparent:hover {
	background-color: transparent !important;
}
/* Game_world>>>subscriber
**************************************/
div.well.pgf-voting-block {
	background-color: transparent !important;
}
/* Alert
**************************************/
.well,
.alert-info,
.alert-error,
.alert-danger,
.alert-success,
#choose-ability-button {
	border: 1px solid gray !important;
}
.alert {
	text-shadow: 0px 0px 3px black, 0px 0px 5px black !important;
	border-radius: 7px !important;
	background-color: transparent !important;
	color: #793 !important;
}
/* Market
**************************************/
.common-card-label {
	color: #999 !important;
}
/* Shop
**************************************/
table.table-striped tbody tr:nth-child(2n+1):hover th {
	color: #A9A9A9 !important;
}
.nav-tabs.nav-stacked > li > a,
.nav-tabs.nav-stacked > li > a:hover {
	border-color: gray !important;
}
.nav-tabs.nav-stacked > li > a:hover {
	border-bottom-right-radius: 0px !important;
	border-bottom-left-radius: 0px !important;
}
.nav-tabs.nav-stacked > li:first-child > a {
	border-top-left-radius: 5px !important;
	border-top-right-radius: 5px !important;
}
.nav-tabs.nav-stacked>li:last-child>a {
	border-bottom-right-radius: 5px !important;
	border-bottom-left-radius: 5px !important;
}
.offset2 {
	padding-left: 15px !important;
	padding-bottom: 15px !important;
	margin-top: 1em !important;
	border-radius: 10px !important;
	background-color: transparent !important;
	border: 3px double gray !important;
}
hr.offset2 {
	display:none
}
/* Popover
**************************************/
.popover-inner {
	padding: 3px !important;
	background: gray !important;
	border-radius: 7px;
}
.popover-title {
	border-bottom: 2px solid gray !important;
}
/* Tables
**************************************/
.table-bordered {
	border-color: gray !important;
}
.table {
	margin-bottom: 3px !important;
}
table {
	background-color: transparent !important;
}
.table th,
.table td {
	padding: 4px !important;
	border-collapse: collapse !important;
	border: 1px solid gray !important;
	background-color: transparent !important;
	color: #A9A9A9 !important;
	transition: color 0.3s;
}
.table tbody tr:hover th,
.table tbody tr:hover td {
	color: #793 !important;
}
.hero-ability-empty-record:hover {
	background-color: transparent !important;
}
table.table tbody tr:nth-child(odd):hover td,
table.table tbody tr:nth-child(odd):hover th,
table.table tbody tr:nth-child(even):hover td,
table.table tbody tr:nth-child(even):hover th,
table.table.table-highlighting tbody tr:nth-child(odd):hover td,
table.table.table-highlighting tbody tr:nth-child(odd):hover th,
table.table.table-highlighting tbody tr:nth-child(even):hover td,
table.table.table-highlighting tbody tr:nth-child(even):hover th {
	color: #793 !important;
}
/* Buttons
**************************************/
.btn {
	color: #333 !important;
	transition: color 0.7s;
}
.btn:hover {
	color: #08C !important;
}
/* Map_commands_bar
**************************************/
#pgf-map-block .map-commands {
	top: 1px !important;
	right: 1px !important;
	border: 3px double #A9A9A9 !important;
	background-color: transparent !important;
}
.dropdown-menu a {
	color: #793 !important;
}
/* Modal_dialog
**************************************/
.modal {
	top: 45%  !important;
}
h4 small {
	color: #783 !important;
}
.tabs-left > .nav-tabs .active > a,
.tabs-left > .nav-tabs > li > a:hover {
	border-top-right-radius: 0px !important;
	border-bottom-right-radius: 0px !important;
}
.modal-header {
	border-bottom: 2px solid gray !important;
}
.modal-footer {
	border-top: 1px solid #A9A9A9;
	box-shadow: none  !important;
}
.tabs-left > .nav-tabs {
	border-right: 1px solid gray !important;
}
.table-bordered {
	border-collapse: collapse !important;
}
table.table-no-highlighting.table tbody tr:hover td {
	background-color: transparent !important;
}
.place-description-dialog .person-record {
	margin-bottom: -14px  !important;
	margin-right: 10px  !important;
}
a.info-link {
	border-bottom: none !important;
}
.hero-preference-choise.choosen,
.hero-preference-choise.choosen:hover {
	background-color: transparent !important;
}
/* LoginFix
**************************************/
#pgf-login-form {
	-webkit-box-shadow: none !important;
	box-shadow: none !important;
}
/* linguisticsFix
**************************************/
.btn-toolbar > form:nth-child(2) > input:nth-child(1),
.btn-toolbar > form:nth-child(3) > input:nth-child(1) {
	padding: 5px !important;
}
/* Spoiler
**************************************/
.accordion-inner {
	border-top: 1px solid #999 !important;
}
.accordion-group {
	border: 1px solid #999 !important;
}
/* Forum
**************************************/
.forum-table .new-messages a {
	color: #793 !important;
}
.forum-table .threads-count,
.forum-table .posts-count {
	text-align: center !important;
	vertical-align: middle !important;
}
.breadcrumb {
	border: 3px double gray !important;
	background-color: transparent !important;
	background-image: none !important;
	box-shadow:  none !important;
}
.breadcrumb li {
	text-shadow: 0px 0px 3px black, 0px 0px 5px black !important;
}

.post .info,
.post .head,
.post .info-head {
	background-color: transparent !important;
	border: 1px solid gray !important;
}
blockquote {
	border-left: none !important;
}
.forum-table .thread-page-links {
	color: #A9A9A9 !important;
}
form .control-group textarea,
form .control-group input {
	margin-top: 7px !important;
	color: #FFF !important;
	border-color: gray !important;
	background-color: transparent !important;
}
.pgf-quote,
.pgf-scroll-top,
.pgf-quote-short,
.pgf-complaint-button,
.pgf-change-post-button,
.pgf-remove-post-button {
	font-size: 0px !important;
}
.pgf-quote:before {
	content: url('data:image/png;base64,');
}
.pgf-quote:hover:before {
	content: url('data:image/png;base64,');
}
.pgf-scroll-top:before {
	content: url('data:image/png;base64,');
}
.pgf-scroll-top:hover:before {
	content: url('data:image/png;base64,');
}
.pgf-quote-short:before {	
	content: url('data:image/png;base64,');
