css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("w3schools.com") {

html, #main, .w3-sidebar, .w3-light-grey,
.w3-container, .top, .w3-white
{
background-color: #111111 !important;
}
.w3-example, th,
.nextprev a:link, .nextprev a:visited
{
background-color: #333333 !important;
}
.w3-code, .w3-note, #leftmenuinnerinner a:hover, td
{
background-color: #555555 !important;
}
.w3-code
{
border-left:4px #FFFF00 solid !important;
}
.nextprev a
{
border:1px #333333 solid !important;
}
hr, table, tr, td, th
{
border-color:#FFFF00 !important;
}
h1, h2, h3, h4, h5, .w3-example span, a:hover, 
.dotcom, .w3-code, .w3-code span, th
{
color: #FFFF00 !important;
}
a, p, li, td,
.w3-xlarge, .w3-text-dark-grey
{
color: #FFFFFF !important;
}
}
