css:
@-moz-document domain("dilbert.com"){
  body {
      position: absolute;
      top: 0;
      left: 0;
      height: 100%;
      width: 100%;
  }
  div.Header,
  div.FAV_MyStrips,
  div.HPG_Promo_Template.HPG_Promo_Template_5,
  div.HPG_Promo.HPG_Promo_E,
  div.FTR_FooterTop,
  div.FTR_Footer,
  div.Col_B,
  div.Clear,
  a#strip_zoom.STR_Zoom,
  div.STR_Image img,
  div.STR_Search,
  div.STR_Footer,
  div.STR_StripComments,
  div.HPG_Promo.HPG_Promo_U,
  .cc_banner-wrapper {
      display: none !important;
  }
  div.TransparentBackground.Content {
      padding: 0 !important;
  }
  div.Col_A,
  div.STR_Header {
      padding: 0 !important;
      margin: 0 !important;
  }
  div.STR_Header,
  div.STR_Container,
  div.FeaturedStripWrapper,
  div.STR_Featured.STR_FeaturedMain,
  div.Col_A,
  div.SiteWrapper {
      width: 1000px !important;
  }
  div.FeaturedStripWrapper {
      min-height: 500px;
  }
  div.STR_Header div[style] {
      position: absolute;
      top: 30px;
      display: block !important;
  }
  .STR_Featured,
  .STR_Container,
  .STR_Header {
      background: #FFF !important;
  }
}
