@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("reddit.com") {
    
    body.moderator .entry .buttons .remove-button a,
    body.listing-page .entry .buttons .remove-button a {
        color: /*[[remove-color]]*/ !important;
    }
    
    body.moderator .entry .buttons .approve-button a,
    body.listing-page .entry .buttons .approve-button a{
        color: /*[[approve-color]]*/ !important;
    }
}
