css:
@-moz-document url-prefix("https://www.tumblr.com") {
/* --- MISC --- */

#dashboard_iframe {
    background: transparent !important;
  }

#glass_overlay.show {
    z-index: 0 !important;
}

.identity .send_fan_mail {
    background: rgba(0,0,0,0.303) !important;
}


/* --- header --- */

.l-header-container {
  z-index: 3000 !important;
  width: 100% !important;
  position: fixed !important;
  top: 0px !important;
  left: 0px !important;
  height: 26px !important;
    box-shadow: none !important;
}

.l-header-container--refresh .l-header {
  max-width: 100% !important;
  padding-right: 50px !important;
    }

#logo .logo_dot {
  top: 2px !important;
  left: 112px !important;
}


.tab_bar .tab.iconic {
          margin-left: 24px !important;
        }

#xkit-control {
  margin-top: -4px !important;
}

.l-header #tabs_outer_container {
  position: fixed !important;
  top: 3px !important;
  left: 27px !important;
  width: 100% !important;
}

.l-header .tab_bar {
  float: left !important;
  width: 100% !important;
  padding: 0px !important;
}

.tab_bar .tab {
  height: 33px !important;
  min-height: 26px !important;
  margin-top: -5px !important;
    }

.l-header-container--refresh .tab_bar .tab.iconic .tab_anchor {
  height: 33px !important;
  opacity: 0.64 !important;
        }
          
          .l-header-container--refresh .tab_bar .tab.selected .tab_anchor, .l-header-container--refresh .tab_bar .tab.active .tab_anchor {
  opacity: 1 !important;
                    }

.l-header .tab_notice {
top: 7px !important;
left: 36px !important;
padding: 1px 2px !important;
border: none !important;
background: transparent !important;
}

.identity-refresh .tab_notice .tab_notice_value {
        color: #fff !important;
      }

.l-header .logo {
position: fixed !important;
margin: 0px !important;
top: 1px !important;
left: -20px !important;
}

.svg-logo {
          padding: 2px 18px 14px 30px !important;
  height: 21px !important;
  width: auto !important;
            }

.small-shadow, .large-shadow {
  opacity: 0 !important;
  }
  
#logo .logo_anchor {
  background-size: 93px 20px !important;
}

.l-header-container--refresh .l-header .compose-button svg {
                        background: transparent;
                        top: -12px !important;
                        height: 19px !important;
                        border: none !important;
                        z-index: 3008 !important;
        position: relative !important;
  fill: #fff !important;
  opacity: 0.6 !important;
                      }

    .compose-button:before {
     	color: rgba(255,255,255,0.61) !important;
     }
                      
  .identity-refresh .glass.blue, .identity-refresh #hello_glass.blue, .identity-refresh #glass_overlay.blue {
        background: rgba(0,0,0,0.5142) !important;
 }

.l-header-container--refresh .l-header .ui_search, .ui_search, .ui_search .search_form_row {
  margin: 0px !important;
        height: 26px !important;
  float: right !important;
      }

.l-header-container--refresh.header-redesign.has-3d-logo .search_popover  {
        top: 30px!important;
  right: 119px!important;
  left: auto !important;
      }

      .ui_search .search_query {
  	color: #fff !important;
    background-color: transparent !important;
  	height: 26px !important;
  	margin-top: 0px !important;
  	border: none !important;
  	box-shadow: none !important;
    border-radius: 0px !important;
    padding-left: 0px !important;
    padding-right: 31px !important;
  }
  
  ::-webkit-input-placeholder {
      color: transparent !important;
    }
    
    :-moz-placeholder {
  color: transparent !important;
    }
    
  .ui_search.active .search_query {
  border-bottom: 1px solid #fff !important;
      }
  
  .ui_search .search_form_field:before {
          left: auto !important;
          right: 0px !important;
          top: -7px !important;
  cursor: pointer !important;
                        

        }

.search_popover {
  top: 28px !important;
  left: auto !important;
  right: 118px !important;
  }

.elevator-wrapper {
  top: 6px !important;
    bottom: auto !important;
  z-index: 3010 !important;
  }

.elevator {
    opacity: 0.4 !important;
    transform: none !important;
    color: #fff !important;
  }
  
  .elevator-wrapper.visible .elevator {
  opacity: 1 !important;
        }

#search_actions_search .elevator, #discover_actions_discover .elevator {
  background: transparent !important;
  line-height: 33px !important;
}


/* --- Posts --- */

.post_avatar.post-avatar--fixed, .post_avatar.post-avatar--sticky .post_avatar_wrapper {
  top: 40px !important;
    }
  
  .post_avatar {
    background: transparent !important;
  }

  .post_avatar .post_avatar_link {
  background-color: rgba(0,0,0,0.412) !important;
  }

.l-content, .l-container.l-container--two-column-dashboard .left_column, .l-container.l-container--two-column-dashboard .right_column, #right_column {
  background-color: transparent !important;
}
  
.l-container--two-column-dashboard {
      padding-top: 72px !important;
    }

#posts.posts>.post_container {
  margin-bottom: 72px !important;
  }

.post_full {
  box-shadow: 0px 4px 76px -4px rgba(0,0,0,0.52) !important;
    padding-top: 0px !important;
  }
  
  #toaster .toast .toast_border {
    background: transparent !important;
  }
  
  .post_full .post_header {
    height: 60px !important;
  	padding-top: 16px !important;
  border-radius: 3px 3px 0px 0px !important;
          }
      
      .identity-refresh .post_full:hover .post_permalink, .identity-refresh .post_full .post_permalink {
  	  top: 6px !important;
      right: 9px !important;
      border-radius: 12px !important;
      border: none !important;
      width: 15px !important;
      height: 15px !important;
    }
        
        .post_avatar, .post_avatar_link, .identity-legacy .controls_section.user_list .avatar, .radar .radar_tumblelog_avatar img {
    border-radius: 50px !important;
  }

.post_full .post_action_wrapper {
      right: 40px !important;
    }

.post_full .post_source {
      right: 10px !important;
    }
    
    .identity .controls_section li:not(.section_header):hover {
            background: rgba(255,255,255,0.221) !important;
          }
          
          .identity .controls_section.user_list li .follow_list_item_blog:before, .radar .radar_footer .radar_avatar:before, .identity .right_column:after {
                    background: transparent !important;
                    border: none !important;
                  }
                  
                  .pinned_sidebar_footer {
                      width: 70% !important;
                    }
  
                    
        .plus-follow-button:before, .plus-follow-button .follow_icon {
                          color: #fff !important;
                        }

.plexi.show.color, .post-forms-glass {
  		background: rgba(0,0,0,0.5142) !important;
      }

#posts.posts>li.notification.single_notification {
  margin: -30px 0px 40px 0px !important;
}

.remnant-unit-container {
    margin-top: -25px !important;
    margin-bottom: 30px !important;
    
  }

div#new-xkit-control {
    margin-top: -4px !important;
}
      
}

/*[[look]]*/

/*[[dock]]*/

/*[[header]]*/
