css:
@-moz-document url-prefix("http://www.anime-realm.com/") {
body {
    background: url(/*[[BGimage]]*/) !important;
    background-size: cover !important;
    background-attachment: fixed !important;
}
.anime_list_result * {
    background-color: transparent !important;
}
td form input {
    background-color: rgba(0,0,0,0.5) !important;
}

#SocialBox {
    background-color: transparent !important;
}



body.hasGoogleVoiceExt {
    background-color: rgba(0,0,0,0.5)!important;
}

#episode {
    background-color: rgba(0,0,0,0.5)!important;
}

#footer {
    background-color: rgba(0,0,0,0.5)!important;
    box-shadow: 5px -2px 5px 3px black!important;
}

div.episode_quick_menu {
    background-color: rgba(0,0,0,0.5)!important;
}

#left {
   background-color: rgba(0,0,0,0.5)!important;
}

div.header {
    background-color: rgba(0,0,0,0.5)!important;
}

#right {
    box-shadow: 3px 3px 10px 3px black!important;
}

tbody tr td {
    background-color: transparent!important;
}

.footer tbody tr td {
    background-color: rgba(0,0,0,0.5)!important;
}

#watch_episode_navigation tbody tr td {
    background-color: rgba(0,0,0,0.5)!important;
}

.episode_advert tbody tr td {
    background-color: rgba(0,0,0,0.5)!important;
}

.logo tbody tr td {
    background-color: transparent!important;
}

#main {
    background-color: rgba(0,0,0,0.5)!important;
}

#cointeiner {
    background-color: rgba(0,0,0,0.5)!important;
}

#main #left #episode .episode .episode_navigation {
    background-color: transparent!important;
}

